/**
 * @title SafeMath
 * @notice this library does not contain the 'mod' functions as they were redundant and not used in the oracle contract
 * @dev Math operations with safety checks that revert on error
 */
library SafeMath {

    /**
     * @dev Returns the addition of two unsigned integers, reverting on overflow.
     * @param a First operand
     * @param b Second operand
     */
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }

    /**
     * @dev Returns the subtraction of two unsigned integers, reverting on underflow
     * (when the result is negative).
     * @param a First operand
     * @param b Second operand
     */
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction underflow");
    }

    /**
     * @dev Returns the subtraction of two unsigned integers, reverting with custom message on underflow
     * (when the result is negative).
     * @param a First operand
     * @param b Second operand
     * @param errorMessage Custom error message
     */
    function sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }

    /**
     * @dev Returns the multiplication of two unsigned integers, reverting on overflow.
     * @param a First operand
     * @param b Second operand
     */
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        // Gas optimization: this is cheaper than requiring 'a' not being zero, but the
        // benefit is lost if 'b' is also tested.
        // See: https://github.com/OpenZeppelin/openzeppelin-contracts/pull/522
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }

    /**
     * @dev Returns the division of two unsigned integers, reverting on division by zero.
     * @param a First operand
     * @param b Second operand
     */
    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }

    /**
     * @dev Returns the division of two unsigned integers, reverting with custom message on division by zero.
     * @param a First operand
     * @param b Second operand
     * @param errorMessage Custom error message
     */
    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        // assert(a == b * c + a % b); // There is no case in which this doesn't hold

        return c;
    }

