pragma solidity 0.8.19;


/* IMPORTS */

import "./Interfaces/Roles.sol";
import "./Interfaces/SafeMath.sol";
import "./Interfaces/ClientInterface.sol";


/* ERRORS */

error Oracle__NotAnOwner();
error Oracle__AlreadyAnOracle();
error Oracle__NotAnOracle();
error Oracle__RemovingLastOracle();
error Oracle__NotInMempool();


/// @title a network of oracles for the mqube private blockchain network
/// @author vidhan.mangla@mqube.com
/// @notice an oracle contract to pull decentralised data from APIs and provide it to other smart contracts on the blockchain
/// @dev will add later if necessary
contract Oracle {


  /* EVENTS */

  event GetLatestOffChainDataEvent(address clientAddress, uint256 id);
  event SetLatestOffChainDataEvent(uint256 offChainData, address clientAddress);
  event AddOracleEvent(address oracleAddress);
  event RemoveOracleEvent(address oracleAddress);
  event SetThresholdEvent(uint256 threshold);
  event EtherReceivedEvent(address sender, uint256 amount);
  event HistoricalDataStoredEvent(uint256 indexed id, uint256 timestamp, uint256 offChainData, address oracleAddress);
  event HistoricalDataClearedEvent(uint256 indexed id, uint256 timestamp);


  /* ROLES LIBRARY */

  using Roles for Roles.Role;
  Roles.Role private owners;
  Roles.Role private oracles;


  /* STATE VARIABLES */

  using SafeMath for uint256;
  uint8 private numOracles = 0;
  uint8 private THRESHOLD = 1;
  /// @dev compute and generate a unique identifier for the offChainData request
  uint256 private randNonce = 0;
  uint256 private modulus = 1000;
  uint256 public maxHistoricalDataSize = 100;


  /* MAPPINGS */

  mapping(uint256=>bool) memPool;
  mapping(uint256=>Response[]) public requestIdToResponse;
  mapping(address=>HistoricalData[]) public historicalData;


  /* STRUCTS */

  struct Response {
    address oracleAddress;
    address clientAddress;
    uint256 offChainData;
  }

  struct HistoricalData {
    uint256 timestamp;
    uint256 offChainData;
  }


  /* MODIFIERS */

  modifier onlyOwner() {
    if (!owners.has(msg.sender)) {
      revert Oracle__NotAnOwner();
    }
    _;
  }


  /* CONSTRUCTOR FUNCTION */

  /// @dev specify the contract owner's address (see deploy script)
  constructor (address _owner) {
    owners.add(_owner);
  }


  /* MAIN FUNCTIONS */

  /// @dev allows the owner to add a new oracle
  function addOracle(address _oracle) public onlyOwner {
    if (oracles.has(_oracle)) {
      revert Oracle__AlreadyAnOracle();
    }
    oracles.add(_oracle);
    numOracles++;
    emit AddOracleEvent(_oracle);
  }

  /// @dev allows the owner to remove an existing oracle
  function removeOracle(address _oracle) public onlyOwner {
    if (!oracles.has(_oracle)) {
      revert Oracle__NotAnOracle();
    }
    if (!(numOracles > 1)) {
      revert Oracle__RemovingLastOracle();
    }
    oracles.remove(_oracle);
    numOracles--;
    emit RemoveOracleEvent(_oracle);
  }

  /// @dev minimum number of oracle responses
  function setThreshold(uint8 _threshold) public {
    if (!owners.has(msg.sender)) {
      revert Oracle__NotAnOwner();
    }
    THRESHOLD = _threshold;
    emit SetThresholdEvent(THRESHOLD);
  }

  /// @dev get the latest offChainData of a given asset from the oracles
  function getLatestOffChainData() public returns (uint256) {
    randNonce++;
    uint256 id = uint256(keccak256(abi.encodePacked(block.timestamp, msg.sender, randNonce))) % modulus;
    memPool[id] = true;
    emit GetLatestOffChainDataEvent(msg.sender, id);
    return id;
  }

  /// @dev helper function to sort the array
  function sort(uint256[] memory arr, uint256 left, uint256 right) internal pure {
      if (left < right) {
          uint256 pivot = arr[right];
          uint256 partitionIndex = partition(arr, left, right, pivot);

          // sort the left and right arrays
          sort(arr, left, partitionIndex - 1);
          sort(arr, partitionIndex + 1, right);
      }
  }

  /// @dev partition function for quicksort
  function partition(uint256[] memory arr, uint256 left, uint256 right, uint256 pivot) internal pure returns (uint256) {
      uint256 i = left;
      for (uint256 j = left; j < right; j++) {
          if (arr[j] < pivot) {
              (arr[i], arr[j]) = (arr[j], arr[i]);
              i++;
          }
      }
      (arr[i], arr[right]) = (arr[right], arr[i]);
      return i;
  }

  /// @dev helper function to calculate percentile
  function percentile(uint256[] memory arr, uint256 perc) internal pure returns (uint256) {
      uint256 len = arr.length;
      uint256 n = len * perc / 100;
      if (n == len) {
          return arr[len - 1];
      }
      return arr[n];
  }

  /// @dev consensus mechanism with removing the outliers by using quartiles and interquartile ranges
  function computeAverageOffChainData(uint256 _id) private view returns (uint256) {
      uint256 len = requestIdToResponse[_id].length;
      if (len == 0) return 0;

      // step 1: sort the responses
      uint256[] memory data = new uint256[](len);
      for (uint256 i = 0; i < len; i++) {
          data[i] = requestIdToResponse[_id][i].offChainData;
      }
      sort(data, 0, len - 1);

      // step 2: calculate quartiles
      uint256 q1 = percentile(data, 25);
      uint256 q3 = percentile(data, 75);

      // step 3: compute interquartile range (IQR)
      uint256 iqr = q3.sub(q1);

      // step 4: identify and remove outliers
      uint256 lowerBound = q1.sub(iqr.mul(3).div(2));
      uint256 upperBound = q3.add(iqr.mul(3).div(2));

      uint256 sum = 0;
      uint256 count = 0;
      for (uint256 i = 0; i < len; i++) {
          if (data[i] >= lowerBound && data[i] <= upperBound) {
              sum = sum.add(data[i]);
              count++;
          }
      }

      // step 5: calculate the average
      if (count == 0) return 0;
      return sum.div(count);
  }

  /// @dev update the latest offChainData of a given asset on the blockchain
  function setLatestOffChainData(uint256 _offChainData, address _clientAddress, uint256 _id) public {
    // if (!oracles.has(msg.sender)) {
    //   revert Oracle__NotAnOracle();
    // }
    if (!memPool[_id]) {
      revert Oracle__NotInMempool();
    }
    requestIdToResponse[_id].push(Response(msg.sender, _clientAddress, _offChainData));
    uint256 numResponses = requestIdToResponse[_id].length;
    if (numResponses == THRESHOLD) {
        uint256 computedOffChainData = computeAverageOffChainData(_id);
        delete memPool[_id];
        delete requestIdToResponse[_id];
        ClientInterface(_clientAddress).callback(computedOffChainData, _id);
        emit SetLatestOffChainDataEvent(computedOffChainData, _clientAddress);
    }
    // store historical data per oracle address
    /// @dev recommend not to use on mainnet, only on MQube private blockchain
    if (historicalData[msg.sender].length >= maxHistoricalDataSize) {
      delete historicalData[msg.sender];
      emit HistoricalDataClearedEvent(_id, block.timestamp);
    }
    HistoricalData memory newData = HistoricalData({
      timestamp: block.timestamp,
      offChainData: _offChainData
    });
    historicalData[msg.sender].push(newData);
    emit HistoricalDataStoredEvent(_id, newData.timestamp, newData.offChainData, msg.sender);
  }

  /// @dev get historical data for a specific oracle
  function getHistoricalData(address oracleAddress) public view returns (HistoricalData[] memory) {
    return historicalData[oracleAddress];
  }

  /// @dev check if historical data for a specific oracle is full
  function isHistoricalDataFull(address oracleAddress) public view returns (bool) {
    return historicalData[oracleAddress].length >= maxHistoricalDataSize;
  }


  /* RECEIVE & FALLBACK FUNCTIONS */

  /// @dev allows the contract to directly receive ether
  receive() external payable {
      emit EtherReceivedEvent(msg.sender, msg.value);
  }

  /// @dev a catch-all for any ether sent to the contract in cases not covered by the receive function
  fallback() external payable {
      emit EtherReceivedEvent(msg.sender, msg.value);
  }


}","keccak256":"0xb4e52d84c1266839826b52f3e361404e52c8219d43abd2eadd5f17db99c83a9c","license":"MIT"}},"version":1}
